import React, { useState, useEffect } from 'react';
import './App.css';
import AdBanner from './AdBanner';
import JobDetails from './components/JobDetails';
import adsData from './adsData';

function App() {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [coverLetter, setCoverLetter] = useState('');
  const [pagination, setPagination] = useState({
    currentPage: 1,
    totalPages: 1,
    pageSize: 5,
    totalJobs: 0,
  });

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('http://localhost:3000/categories');
        if (!response.ok) throw new Error('Error al obtener categorías');
        const categoriesData = await response.json();
        setCategories(categoriesData);
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchCategories();
  }, []);

  const fetchJobs = async (page = 1) => {
    try {
      const response = await fetch(`http://localhost:3000/jobs?page=${page}`);
      if (!response.ok) throw new Error('Error al obtener trabajos');
      const data = await response.json();
      setFilteredJobs(data.jobs);
      setPagination(data.pagination);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  useEffect(() => {
    fetchJobs(pagination.currentPage);
  }, [pagination.currentPage]);

  const handleNextPage = () => {
    if (pagination.currentPage < pagination.totalPages) {
      setPagination((prev) => ({ ...prev, currentPage: prev.currentPage + 1 }));
    }
  };

  const handlePrevPage = () => {
    if (pagination.currentPage > 1) {
      setPagination((prev) => ({ ...prev, currentPage: prev.currentPage - 1 }));
    }
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setPagination({ ...pagination, currentPage: 1 });
    fetchJobs(1);
  };

  const handleViewDetails = (job) => {
    setSelectedJob(job);
    setCoverLetter('');
  };

  return (
    <div className="app-container">
      <Navbar />
      {selectedJob ? (
        <JobDetails job={selectedJob} onBack={() => setSelectedJob(null)} />
      ) : (
        <>
          <header className="app-header">
            <h1>Encuentra tu trabajo ideal</h1>
          </header>
          <section className="category-list">
            {categories.map((category, index) => (
              <button
                key={index}
                onClick={() => handleCategoryClick(category)}
                className="category-button"
              >
                {category}
              </button>
            ))}
          </section>
          <section className="job-list">
            {filteredJobs.map((job) => (
              <JobItem key={job.id} job={job} onViewDetails={() => handleViewDetails(job)} />
            ))}
          </section>
          <div className="pagination-controls">
            <button onClick={handlePrevPage} disabled={pagination.currentPage === 1}>
              Anterior
            </button>
            <span>Página {pagination.currentPage} de {pagination.totalPages}</span>
            <button onClick={handleNextPage} disabled={pagination.currentPage === pagination.totalPages}>
              Siguiente
            </button>
          </div>
        </>
      )}
      <Footer />
    </div>
  );
}

const Navbar = () => (
  <nav className="navbar">
    <div className="navbar-brand">GPTJOB</div>
  </nav>
);

const Footer = () => (
  <footer className="footer">
    <AdBanner position="bottom" />
  </footer>
);

const JobItem = ({ job, onViewDetails }) => (
  <div className="job-item">
    <h2>{job.title}</h2>
    <p>{job.city}</p>
    <button onClick={onViewDetails}>Abrir Detalles</button>
  </div>
);

export default App;
